mapper.update:
update res_comment
agree = agree + ${weight}, disagree = disagree + ${weight}, num_of_vote = num_of_vote + 1
where num=#{com_num} and writer=#{memId}

mapper.insert:
{call
	declare

		numSeq res_comment.num%TYPE;

	begin
		select res_comment_seq.nextval into numSeq from dual;
		insert
			into res_comment
		values
			(numSeq, #{comment.res_num}, #{comment.writer}, #{comment.comments}, #{comment.img}, default, default, default, #{comment.open}, sysdate);
		insert
			into res_point
		values
			(res_point_seq.nextval, #{point.res_num}, numSeq, sysdate, #{point.taste}, #{point.price}, #{point.kindness}, #{point.hygiene});
	end
}

mapper.update:
update member
pw=#{pw},
email=#{email}, mobile=#{mobile}, area1=#{area1}, area2=#{area2}, birth=#{birth}
where id=#{id}
